src/App.css.content:
* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

body {
  font-family: 'Inter', 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
  line-height: 1.6;
  color: #374151;
  overflow-x: hidden;
  background: #fdf2f8;
}

html {
  scroll-behavior: smooth;
}

.portfolio {
  min-height: 100vh;
  background: linear-gradient(135deg, #fdf2f8 0%, #fce7f3 50%, #f3e8ff 100%);
}

/* Navigation - Feminine Design */
.navbar {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  z-index: 1000;
  padding: 1.2rem 0;
  box-shadow: 0 4px 30px rgba(236, 72, 153, 0.1);
  transition: all 0.3s ease;
  border-bottom: 1px solid rgba(236, 72, 153, 0.1);
}

.nav-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.nav-logo {
  display: flex;
  align-items: center;
  gap: 0.8rem;
  cursor: pointer;
}

.logo-image {
  width: 50px;
  height: 50px;
  border-radius: 10px;
  object-fit: cover;
  box-shadow: 0 4px 15px rgba(236, 72, 153, 0.3);
  transition: transform 0.3s ease;
}

.logo-image:hover {
  transform: scale(1.1);
}

.logo-text {
  font-size: 1.4rem;
  font-weight: 600;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.5px;
  margin: 0;
}

.nav-menu {
  display: flex;
  list-style: none;
  gap: 2rem;
}

.nav-link {
  text-decoration: none;
  color: #666;
  font-weight: 500;
  transition: all 0.3s ease;
  cursor: pointer;
  position: relative;
  padding: 0.8rem 1.2rem;
  border-radius: 25px;
  overflow: hidden;
  display: inline-block;
}

.nav-link:hover {
  color: #ec4899;
  transform: translateY(-2px);
}

.nav-link::after {
  content: '';
  position: absolute;
  width: 0;
  height: 2px;
  bottom: 0;
  left: 50%;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  transition: all 0.3s ease;
  transform: translateX(-50%);
  border-radius: 1px;
}

/* Water Droplet Effect */
.water-droplet {
  position: absolute;
  border-radius: 50%;
  background: radial-gradient(circle, rgba(236, 72, 153, 0.3) 0%, rgba(244, 114, 182, 0.2) 40%, rgba(236, 72, 153, 0.1) 70%, transparent 100%);
  pointer-events: none;
  animation: water-ripple 0.6s ease-out;
  transform: scale(0);
  opacity: 1;
}

@keyframes water-ripple {
  0% {
    transform: scale(0);
    opacity: 1;
  }
  50% {
    transform: scale(0.5);
    opacity: 0.8;
  }
  100% {
    transform: scale(1);
    opacity: 0;
  }
}

/* Enhanced water droplet effect with multiple layers */
.nav-link:active::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(236, 72, 153, 0.2);
  transform: translate(-50%, -50%);
  animation: water-splash 0.4s ease-out;
}

@keyframes water-splash {
  0% {
    width: 0;
    height: 0;
    opacity: 1;
  }
  50% {
    width: 60px;
    height: 60px;
    opacity: 0.6;
  }
  100% {
    width: 100px;
    height: 100px;
    opacity: 0;
  }
}

.nav-link:hover::after {
  width: 100%;
}

.nav-toggle {
  display: none;
  flex-direction: column;
  background: none;
  border: none;
  cursor: pointer;
}

.nav-toggle span {
  width: 25px;
  height: 3px;
  background: #333;
  margin: 3px 0;
  transition: 0.3s;
  border-radius: 2px;
}

/* Hero Section - Feminine Design */
.hero {
  min-height: 100vh;
  background: linear-gradient(135deg, #fdf2f8 0%, #fce7f3 30%, #f8fafc 70%, #fdf2f8 100%);
  display: flex;
  align-items: center;
  padding-top: 100px;
  position: relative;
  overflow: hidden;
}

.hero::before {
  content: '';
  position: absolute;
  top: 10%;
  right: 10%;
  width: 400px;
  height: 400px;
  background: radial-gradient(circle, rgba(236, 72, 153, 0.1) 0%, rgba(219, 39, 119, 0.05) 50%, transparent 100%);
  border-radius: 50%;
  animation: float 8s ease-in-out infinite;
}

.hero::after {
  content: '';
  position: absolute;
  bottom: 20%;
  left: 5%;
  width: 250px;
  height: 250px;
  background: radial-gradient(circle, rgba(168, 85, 247, 0.08) 0%, rgba(147, 51, 234, 0.04) 50%, transparent 100%);
  border-radius: 50%;
  animation: float 6s ease-in-out infinite reverse;
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

.hero-container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
  width: 100%;
  position: relative;
  z-index: 1;
}

.hero-content {
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 4rem;
  align-items: center;
  color: #374151;
  animation: fadeInUp 1s ease;
  position: relative;
  z-index: 1;
}

.hero-image {
  text-align: center;
  animation: slideInFromLeft 1s ease;
}

.hero-photo {
  width: 320px;
  height: 320px;
  border-radius: 50%;
  object-fit: cover;
  box-shadow: 0 25px 50px rgba(236, 72, 153, 0.15);
  border: 6px solid rgba(255, 255, 255, 0.8);
  animation: zoomIn 1s ease, float 6s ease-in-out infinite;
  transition: transform 0.3s ease;
  position: relative;
}

.hero-photo::before {
  content: '';
  position: absolute;
  top: -10px;
  left: -10px;
  right: -10px;
  bottom: -10px;
  background: linear-gradient(45deg, #ec4899, #f472b6, #a855f7, #ec4899);
  border-radius: 50%;
  z-index: -1;
  opacity: 0.1;
  animation: rotate 10s linear infinite;
}

@keyframes rotate {
  0% { transform: rotate(0deg); }
  100% { transform: rotate(360deg); }
}

.hero-photo:hover {
  transform: scale(1.05);
}

.hero-text {
  text-align: left;
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.hero-title {
  font-size: 3.8rem;
  font-weight: 700;
  margin-bottom: 1rem;
  background: linear-gradient(45deg, #1f2937, #374151);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  animation: slideInFromRight 1s ease 0.3s both;
  line-height: 1.2;
  letter-spacing: -1px;
}

.hero-subtitle {
  font-size: 2.2rem;
  font-weight: 400;
  margin-bottom: 1rem;
  color: #ec4899;
  animation: slideInFromRight 1s ease 0.5s both;
  font-style: italic;
}

.hero-badges {
  display: flex;
  gap: 1rem;
  margin-bottom: 2rem;
  flex-wrap: wrap;
  animation: slideInFromRight 1s ease 0.7s both;
}

.badge {
  background: linear-gradient(45deg, #ec4899, #f472b6);
  color: white;
  padding: 0.5rem 1.5rem;
  border-radius: 25px;
  font-size: 0.9rem;
  font-weight: 600;
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.3);
  transition: all 0.3s ease;
  cursor: pointer;
}

.badge:hover {
  transform: translateY(-3px);
  box-shadow: 0 12px 30px rgba(236, 72, 153, 0.4);
}

@keyframes slideInFromLeft {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.hero-description {
  font-size: 1.3rem;
  margin-bottom: 2.5rem;
  max-width: 650px;
  color: #6b7280;
  animation: slideInFromRight 1s ease 0.6s both;
  line-height: 1.7;
}

@keyframes slideInFromRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.hero-buttons {
  display: flex;
  gap: 1rem;
  justify-content: center;
  flex-wrap: wrap;
  animation: fadeIn 1s ease 0.9s both;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.btn {
  padding: 1rem 2rem;
  border: none;
  border-radius: 50px;
  font-size: 1rem;
  font-weight: 600;
  cursor: pointer;
  transition: all 0.3s ease;
  text-decoration: none;
  display: inline-block;
  position: relative;
  overflow: hidden;
}

.btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
  transition: left 0.5s;
}

.btn:hover::before {
  left: 100%;
}

.btn-primary {
  background: linear-gradient(45deg, #ec4899, #f472b6);
  color: white;
  border: 2px solid transparent;
  backdrop-filter: blur(10px);
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.3);
}

.btn-primary:hover {
  background: linear-gradient(45deg, #db2777, #ec4899);
  transform: translateY(-3px);
  box-shadow: 0 12px 30px rgba(236, 72, 153, 0.4);
}

.btn-secondary {
  background: rgba(255, 255, 255, 0.9);
  color: #ec4899;
  border: 2px solid #ec4899;
  backdrop-filter: blur(10px);
}

.btn-secondary:hover {
  background: #ec4899;
  color: white;
  transform: translateY(-3px);
  box-shadow: 0 12px 30px rgba(236, 72, 153, 0.3);
}

/* Sections - Feminine Design */
.section {
  padding: 6rem 0;
  position: relative;
  background: rgba(255, 255, 255, 0.6);
  backdrop-filter: blur(10px);
}

.section:nth-child(even) {
  background: linear-gradient(135deg, rgba(253, 242, 248, 0.8) 0%, rgba(252, 231, 243, 0.6) 100%);
}

.container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 2rem;
}

.section-title {
  font-size: 3rem;
  text-align: center;
  margin-bottom: 3.5rem;
  background: linear-gradient(45deg, #1f2937, #374151);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  position: relative;
  animation: fadeInUp 0.8s ease;
  font-weight: 700;
  letter-spacing: -1px;
}

.section-title::after {
  content: '';
  position: absolute;
  bottom: -15px;
  left: 50%;
  transform: translateX(-50%);
  width: 120px;
  height: 4px;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  border-radius: 2px;
}

/* About Section */
.about-content {
  display: grid;
  grid-template-columns: 1fr 2fr;
  gap: 4rem;
  align-items: center;
  margin-top: 2rem;
}

.about-image {
  text-align: center;
}

.profile-photo {
  width: 300px;
  height: 350px;
  border-radius: 20px;
  object-fit: cover;
  box-shadow: 0 20px 40px rgba(236, 72, 153, 0.3);
  animation: zoomIn 1s ease, float 6s ease-in-out infinite;
  transition: transform 0.3s ease;
  border: 4px solid rgba(255, 255, 255, 0.9);
}

.profile-photo:hover {
  transform: scale(1.05);
}

@keyframes zoomIn {
  from {
    opacity: 0;
    transform: scale(0.8);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.about-text {
  animation: slideInFromRight 1s ease;
}

.about-details p {
  margin-bottom: 1rem;
  font-size: 1.1rem;
  color: #666;
}

.about-details strong {
  color: #ec4899;
  font-weight: 600;
}

.about-description {
  margin-top: 2rem;
  font-style: italic;
  color: #555;
  line-height: 1.8;
}

/* Skills Section */
.skills-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
}

.skill-card {
  background: rgba(255, 255, 255, 0.9);
  padding: 2.5rem;
  border-radius: 25px;
  box-shadow: 0 15px 40px rgba(236, 72, 153, 0.1);
  text-align: center;
  transition: all 0.4s ease;
  position: relative;
  overflow: hidden;
  animation: slideInFromBottom 0.8s ease;
  border: 1px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(10px);
}

@keyframes slideInFromBottom {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.skill-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(108, 99, 255, 0.1), transparent);
  transition: left 0.5s;
}

.skill-card:hover::before {
  left: 100%;
}

.skill-card:hover {
  transform: translateY(-15px) rotate(2deg);
  box-shadow: 0 25px 50px rgba(236, 72, 153, 0.2);
}

.skill-icon {
  font-size: 3rem;
  margin-bottom: 1rem;
  display: flex;
  align-items: center;
  justify-content: center;
  height: 60px;
}

.skill-icon img {
  width: 50px;
  height: 50px;
  object-fit: contain;
  transition: transform 0.3s ease;
}

.skill-card:hover .skill-icon img {
  transform: scale(1.1);
}

.skill-card h3 {
  color: #ec4899;
  margin-bottom: 1.2rem;
  font-size: 1.4rem;
  font-weight: 600;
}

.skill-progress {
  width: 100%;
  height: 8px;
  background: #f0f0f0;
  border-radius: 10px;
  overflow: hidden;
  margin-top: 1rem;
}

.progress-bar {
  height: 100%;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  border-radius: 10px;
  transition: width 2s ease;
  position: relative;
}

.progress-bar::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
  animation: shimmer 2s infinite;
}

@keyframes shimmer {
  0% { transform: translateX(-100%); }
  100% { transform: translateX(100%); }
}

/* Projects Section */
.projects-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  margin-top: 3rem;
}

.project-card {
  background: rgba(255, 255, 255, 0.9);
  border-radius: 25px;
  box-shadow: 0 15px 40px rgba(236, 72, 153, 0.1);
  overflow: hidden;
  transition: all 0.4s ease;
  animation: slideInFromBottom 0.8s ease;
  border: 1px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(10px);
}

.project-card:hover {
  transform: translateY(-15px) rotate(1deg);
  box-shadow: 0 25px 50px rgba(236, 72, 153, 0.2);
}

.project-image {
  height: 220px;
  background: linear-gradient(45deg, #ec4899, #f472b6, #a855f7);
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
}

.project-image::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: linear-gradient(45deg, transparent 30%, rgba(255, 255, 255, 0.1) 50%, transparent 70%);
  transform: translateX(-100%);
  transition: transform 0.6s ease;
}

.project-card:hover .project-image::before {
  transform: translateX(100%);
}

.placeholder-image {
  font-size: 4rem;
  color: white;
  opacity: 0.8;
}

.project-content {
  padding: 2rem;
}

.project-content h3 {
  color: #ec4899;
  margin-bottom: 1.2rem;
  font-size: 1.4rem;
  font-weight: 600;
}

.project-content p {
  color: #666;
  margin-bottom: 1.5rem;
  line-height: 1.6;
}

.project-links {
  display: flex;
  gap: 1rem;
}

.project-link {
  padding: 0.7rem 1.5rem;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  color: white;
  text-decoration: none;
  border-radius: 30px;
  font-size: 0.9rem;
  font-weight: 500;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(236, 72, 153, 0.3);
}

.project-link:hover {
  background: linear-gradient(45deg, #db2777, #ec4899);
  transform: translateY(-3px);
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.4);
}

/* Contact Section */
.contact-intro {
  text-align: center;
  font-size: 1.2rem;
  color: #666;
  margin-bottom: 3rem;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.contact-content {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  margin-top: 3rem;
}

.contact-info {
  text-align: center;
}

.contact-photo {
  margin-bottom: 2rem;
}

.contact-profile-photo {
  width: 150px;
  height: 150px;
  border-radius: 50%;
  object-fit: cover;
  box-shadow: 0 15px 30px rgba(0, 0, 0, 0.2);
  animation: zoomIn 1s ease;
}

.contact-item {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 2rem;
  padding: 1.5rem;
  background: rgba(255, 255, 255, 0.8);
  border-radius: 15px;
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.1);
  transition: transform 0.3s ease;
}

.contact-item:hover {
  transform: translateY(-5px);
}

.contact-icon {
  font-size: 2rem;
  width: 60px;
  height: 60px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  border-radius: 50%;
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.3);
}

.contact-text h4 {
  margin: 0 0 0.5rem 0;
  color: #333;
  font-size: 1.1rem;
  font-weight: 600;
}

.contact-text p {
  margin: 0;
  color: #666;
  font-size: 1rem;
}

.contact-link {
  color: #666;
  text-decoration: none;
  transition: color 0.3s ease;
  display: block;
}

.contact-link:hover {
  color: #ec4899;
  text-decoration: underline;
}

.social-links {
  margin-top: 2rem;
}

.social-links h4 {
  margin-bottom: 1rem;
  color: #333;
  font-size: 1.1rem;
  font-weight: 600;
}

.social-icons {
  display: flex;
  gap: 1rem;
  justify-content: center;
}

.social-icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  text-decoration: none;
  transition: all 0.3s ease;
  box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
}

.social-link img {
  width: 20px;
  height: 20px;
  object-fit: contain;
  filter: brightness(0) invert(1);
}

.social-link svg {
  width: 20px;
  height: 20px;
  fill: currentColor;
}

.social-icon.facebook {
  background: #3b5998;
  color: white;
}

.social-icon.instagram {
  background: linear-gradient(45deg, #405de6, #5851db, #833ab4, #c13584, #e1306c, #fd1d1d);
  color: white;
}

.social-icon.github {
  background: #333;
  color: white;
}

.social-icon:hover {
  transform: translateY(-3px) scale(1.1);
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
}

.contact-form {
  background: rgba(255, 255, 255, 0.9);
  padding: 2.5rem;
  border-radius: 25px;
  box-shadow: 0 15px 40px rgba(236, 72, 153, 0.1);
  animation: slideInFromRight 1s ease;
  border: 1px solid rgba(255, 255, 255, 0.3);
  backdrop-filter: blur(10px);
}

.contact-form h3 {
  margin: 0 0 2rem 0;
  color: #333;
  font-size: 1.5rem;
  font-weight: 600;
}

.form-row {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 1rem;
}

.form-group label {
  display: block;
  margin-bottom: 0.5rem;
  color: #333;
  font-weight: 500;
  font-size: 0.9rem;
}

.form-group {
  margin-bottom: 1.5rem;
}

.form-group input,
.form-group textarea {
  width: 100%;
  padding: 1rem;
  border: 2px solid #f0f0f0;
  border-radius: 10px;
  font-size: 1rem;
  transition: all 0.3s ease;
  background: #fafafa;
}

.form-group input:focus,
.form-group textarea:focus {
  outline: none;
  border-color: #ec4899;
  background: white;
  box-shadow: 0 0 0 3px rgba(236, 72, 153, 0.1);
}

.contact-form .btn {
  width: 100%;
  background: linear-gradient(45deg, #ec4899, #f472b6);
  color: white;
  border: none;
  font-weight: 600;
  box-shadow: 0 8px 25px rgba(236, 72, 153, 0.3);
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 1.2rem 2rem;
